package thinking._04_control.ForEachString;

public class ForEachString {
	public ForEachString() {
		// TODO Auto-generated constructor stub
		for (char c : "An African Swallow".toCharArray())
			System.out.print(c + " ");
	}
} /* Output
A n   A f r i c a n   S w a l l o w 
*/
